mapper.select:
SELECT COUNT(*)
FROM BOARD_LIKE
WHERE BOARD_TYPE = #{boardType}
  AND BOARD_NO = #{boardNo}
  AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM BOARD_LIKE
WHERE BOARD_TYPE = #{boardType}
  AND BOARD_NO = #{boardNo}
  AND MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*)
FROM BOARD_LIKE
WHERE BOARD_TYPE = #{boardType}
  AND BOARD_NO = #{boardNo}
