mapper.insert:
INSERT INTO POST_IMAGE (
    IMAGE_NO, BOARD_TYPE, BOARD_NO,
    IMAGE_PATH, ORIGINAL_NAME, SAVED_NAME, IS_THUMBNAIL
) VALUES (
    SEQ_IMAGE_NO.NEXTVAL,
    #{boardType}, #{boardNo},
    #{imagePath}, #{originalName}, #{savedName}, #{isThumbnail}
)

mapper.update:
UPDATE POST_IMAGE
SET DELETE_YN = 'Y'
WHERE BOARD_TYPE = #{boardType}
  AND BOARD_NO = #{boardNo}
  AND DELETE_YN = 'N'
