mapper.select:
SELECT COUNT(*) 
FROM BOOKMARK
WHERE MEMBER_NO = #{memberNo}
  AND TARGET_TYPE = #{targetType}
  AND TARGET_NO = #{targetNo}

mapper.delete:
DELETE FROM BOOKMARK
WHERE MEMBER_NO = #{memberNo}
  AND TARGET_TYPE = #{targetType}
  AND TARGET_NO = #{targetNo}

mapper.select:
SELECT COUNT(*)
FROM BOOKMARK
WHERE TARGET_TYPE = #{targetType}
  AND TARGET_NO = #{targetNo}

SELECT COUNT(*)
FROM BOOKMARK b
WHERE (
    (b.TARGET_TYPE = 'community' AND b.TARGET_NO IN (
        SELECT POST_NO FROM FREE WHERE MEMBER_NO = #{memberNo}
    ))
    OR
    (b.TARGET_TYPE = 'review' AND b.TARGET_NO IN (
        SELECT REVIEW_NO FROM REVIEW WHERE MEMBER_NO = #{memberNo}
    ))
)

SELECT B.BOOKMARK_NO, B.MEMBER_NO, B.TARGET_TYPE, B.TARGET_NO, B.BOOKMARK_DATE, F.POST_TITLE AS TITLE, F.WRITE_DATE AS WRITE_DATE FROM BOOKMARK B JOIN FREE F ON B.TARGET_TYPE = 'free' AND B.TARGET_NO = F.POST_NO WHERE B.MEMBER_NO = #{memberNo}
UNION ALL
SELECT B.BOOKMARK_NO, B.MEMBER_NO, B.TARGET_TYPE, B.TARGET_NO, B.BOOKMARK_DATE, R.REVIEW_TITLE AS TITLE, R.WRITE_DATE AS WRITE_DATE FROM BOOKMARK B JOIN REVIEW R ON B.TARGET_TYPE = 'review' AND B.TARGET_NO = R.REVIEW_NO WHERE B.MEMBER_NO = #{memberNo}
ORDER BY BOOKMARK_DATE DESC
